(()=>{if(window.busuanziRequestSent)return;window.busuanziRequestSent=true;const u=new URL('https://cdn.busuanzi.cc/');fetch(u.protocol+'//'+u.host+'/abbr-api.php',{method:'POST',body:JSON.stringify({url:location.href,referrer:document.referrer})}).then(r=>r.json()).then(r=>{for(const k in r)document.querySelectorAll('#'+k).forEach(e=>e.innerText=r[k])}).catch(e=>console.error(e))})()